import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { UserContext } from "../context/UserContext";
import { useContext } from "react";
import { auth } from "../firebase-config";
import { onAuthStateChanged, signOut } from "firebase/auth";
import UINavBar from "../pages/html/navbar/UINavBarUnloged";

export default function NavBar() {
  const navigate = useNavigate();
  let [navbar, setNavBar] = useState();
  const { currentUser } = useContext(UserContext);

  const logOut = async () => {
    try {
      await signOut(auth);
      navigate("/");
    } catch {
      alert("For some reasons we can't deconnect you from your account");
    }
  };

  useEffect(() => {
    onAuthStateChanged(auth, () => {
      if (currentUser == null) {
        setNavBar(
          <nav
            className="navbar navbar-light bg-light px'4"
            style={{ width: "100%" }}
          >
            <div>
              <button
                onClick={() => navigate("/")}
                className="btn btn-primary ms-2"
              >
                Accueil
              </button>
              <button
                onClick={() => navigate("/signin")}
                className="btn btn-primary ms-2"
              >
                Connectez-vous
              </button>
              <button
                onClick={() => navigate("/signup")}
                className="btn btn-primary ms-2"
              >
                Cr√©ez votre compte
              </button>
            </div>
          </nav>
        );
      } else {
        setNavBar(
          <nav
            className="navbar navbar-light bg-light px'4"
            style={{ width: "100%" }}
          >
            <div>
              <button
                onClick={() => navigate("/")}
                className="btn btn-primary ms-2"
              >
                Accueil
              </button>
              <button
                onClick={() => navigate("/private/account")}
                className="btn btn-primary ms-2"
              >
                Votre compte
              </button>
              <button onClick={logOut} className="btn-danger ms-2">
                Deconnection
              </button>
            </div>
          </nav>
        );
      }
    });
  }, []);

  return <div>{navbar}</div>;
}
