import React, { useState, useContext } from "react";
import { useRef } from "react";
import { UserContext } from "../context/UserContext";
import { useNavigate } from "react-router-dom";
import { db } from "../firebase-config";
import { setDoc, doc } from "firebase/firestore";

export default function SignUp() {
  const { currentUser, signUp } = useContext(UserContext);
  const [validation, setValidation] = useState("");
  const navigate = useNavigate();

  const inputs = useRef([]);
  const addInput = (el) => {
    if (el && !inputs.current.includes(el)) {
      //récupère tous les inputs
      inputs.current.push(el);
    }
  };

  const dataPerso = useRef([]);
  const addDataPerso = (el) => {
    if (el && !dataPerso.current.includes(el)) {
      //récupère tous les inputs
      dataPerso.current.push(el);
    }
  };

  const formRef = useRef();

  const handleForm = async (e) => {
    e.preventDefault();

    if (inputs.current[1].value.length < 6) {
      setValidation("6 characters minimum");
      return;
    } else if (inputs.current[1].value !== inputs.current[2].value) {
      setValidation("Les mots de passe ne sont pas identiques");
      return;
    }

    //console.log("PRIVATE", currentUser.email);

    try {
      const email = inputs.current[0].value;
      const pwd = inputs.current[1].value;
      const cred = await signUp(email, pwd); //attend une réponse de la requete signUp dans UserContext
      //console.log(cred.user.email);

      const addUser = async () => {
        try {
          const docRef = await setDoc(doc(db, "users", cred.user.email), {
            name: dataPerso.current[0].value.toUpperCase(),
            surName: dataPerso.current[1].value.toUpperCase(),
          });
        } catch (e) {
          console.error("Error adding document: ", e);
        }
      };
      formRef.current.reset();
      navigate("/private/account/");
    } catch {}
  };

  return (
    <>
      <div className="container text-center">
        <h1 className="text-light mb-5">Créez votre compte dès maintenant</h1>
        <div>
          <form ref={formRef} className="sign-up-form" onSubmit={handleForm}>
            <div>
              <p className="form-label text-light">Saisissez votre email:</p>
              <input ref={addInput} type="email" required />
            </div>

            <div>
              <p className="form-label text-light">
                Saisissez votre mot de passe (6 caractères min)
              </p>
              <input ref={addInput} type="password" required />
            </div>

            <div>
              <p className="form-label text-light">
                Confirmez votre mot de passe
              </p>
              <input ref={addInput} type="password" />
            </div>
            <p className="text-danger">{validation}</p>

            <h2 className="text-light">Vos données personnelles</h2>

            <div>
              <p className="form-label text-light">Nom</p>
              <input ref={addDataPerso} type="text" />
            </div>

            <div>
              <p className="form-label text-light">Prénom</p>
              <input ref={addDataPerso} type="text" />
            </div>

            <button className="btn btn-primary mt-3">Valider</button>
          </form>
        </div>
      </div>
    </>
  );
}
