import React, { useState, useContext } from "react";
import {auth} from "../firebase-config"
import { createUserWithEmailAndPassword } from "firebase/auth";
import { useRef } from "react";
import { UserContext } from "../context/UserContext";

export default function SignUp() {

  const [validation, setValidation] = useState("")
  const { currentUser } = useContext(UserContext);

  const inputs = useRef([])
  const addInput = (el) => {
    if (el && !inputs.current.includes(el)) {
      //récupère tous les inputs
      inputs.current.push(el);
    }
  }

  const handleForm = async (e) => {
    e.preventDefault();

    if ((inputs.current[1].value.length || inputs.current[2].value.length) < 6) {
      setValidation("6 characters min");
      return;
    } else if (inputs.current[1].value !== inputs.current[2].value) {
      setValidation("Passwords not indentics");
      return;
    }

    //console.log("PRIVATE", currentUser.email);

    try {
      const cred = await signUp(inputs.current[0].value, inputs.current[1].value); //attend une réponse de la requete signUp dans UserContext
      const name = inputs.current[3].value;
      //formRef.current.reset();
      //console.log(cred.user.email);
      //navigate("/private/private-home");
  } catch {}

  }

  return (
    <div className="container text-center">
      <h1 className="text-light mb-5">Créez votre compte dès maintenant</h1>
      <div>
        <form className="sign-up-form">
        <div>
          <p className="form-label text-light">Saisissez votre email:</p>
          <input ref={addInput} type="email" required></input>
        </div>

        <div>
        <p className="form-label text-light">Saisissez votre mot de passe (6 caractères min)</p>
          <input ref={addInput} type="password" required></input>
        </div>

        <div>
        <p className="form-label text-light">Confirmez votre mot de passe</p>
          <input ref={addInput} type="password"></input>
        </div>
        <p className="text-danger">{validation}</p>
        <button onClick={signUp} className="btn btn-primary mt-3">Valider</button>
        </form>
      </div>
    </div>
  );

}
