import React, { useEffect } from "react";
import { signOut } from "firebase/auth";
import { useNavigate } from "react-router-dom";
import { auth } from "../../../firebase-config";
import { UserContext, userContext } from "../../../context/UserContext";
import { useContext, useState } from "react";
import { db } from "../../../firebase-config";
import { doc, getDoc } from "firebase/firestore";
import { async } from "@firebase/util";

export default function AccountHome() {
  const navigate = useNavigate();
  const { currentUser } = useContext(UserContext);
  let [infoPerso, setInfoPerso] = useState({});
  let [options, setOptions] = useState();

  const logOut = async () => {
    try {
      await signOut(auth);
      navigate("/");
    } catch {
      alert("For some reasons we can't deconnect you from your account");
    }
  };

  const readResult = async () => {
    const docRef = doc(db, "users", currentUser.email);
    const docSnap = await getDoc(docRef);
    //console.log(docSnap.data());
    if (docSnap.data().classe === "Seconde") {
      const docRef = doc(db, "users", currentUser.email);
      const docSnap = await getDoc(docRef);
      infoPerso = {
        mail: docSnap.data().mail,
        surName: docSnap.data().surName,
        name: docSnap.data().name,
        classe: docSnap.data().classe,
      };
    } else if (docSnap.data().classe === "Premiere") {
      const docRef = doc(db, "users", currentUser.email);
      const docSnap = await getDoc(docRef);
      infoPerso = {
        mail: docSnap.data().mail,
        surName: docSnap.data().surName,
        name: docSnap.data().name,
        classe: docSnap.data().classe,
        option1: docSnap.data().option1,
        option2: docSnap.data().option2,
        option3: docSnap.data().option3,
      };

      setOptions(
        <div>
          <h2 className="text-light">Avec comme options:</h2>
          <ul>
            <li className="text-light">{infoPerso.option1}</li>
            <li className="text-light">{infoPerso.option2}</li>
            <li className="text-light">{infoPerso.option3}</li>
          </ul>
        </div>
      );
    } else if (docSnap.data().classe === "Terminale") {
      const docRef = doc(db, "users", currentUser.email);
      const docSnap = await getDoc(docRef);
      setInfoPerso({
        mail: docSnap.data().mail,
        surName: docSnap.data().surName,
        name: docSnap.data().name,
        classe: docSnap.data().classe,
        option1: docSnap.data().option1,
        option2: docSnap.data().option2,
      });

      setOptions(
        <div>
          <h2 className="text-light">Avec comme options:</h2>
          <ul>
            <li className="text-light">{infoPerso.option1}</li>
            <li className="text-light">{infoPerso.option2}</li>
          </ul>
        </div>
      );
    }
  };

  useEffect(() => {
    readResult();
  }, []);

  return (
    <div className="container">
      <h1 className="text-light">
        Bonjour, {infoPerso.surName} {infoPerso.name}
      </h1>
      <h2 className="text-light">
        Vous êtes en classe de : {infoPerso.classe}
      </h2>
      {options}
      <button className="btn-danger" onClick={logOut}>
        deconnection
      </button>
    </div>
  );
}
