import React from "react";
import { signOut } from "firebase/auth";
import { useNavigate } from "react-router-dom";
import { auth } from "../../../firebase-config";
import { UserContext, userContext } from "../../../context/UserContext";
import { useContext, useState } from "react";
import { db } from "../../../firebase-config";
import { collection, query, where, getDocs } from "firebase/firestore";

export default function AccountHome() {
  const navigate = useNavigate();
  const { currentUser } = useContext(UserContext);
  const [affichage, setAffichage] = useState("");
  const users = collection(db, "users"); //on recupere la collection des users
  const resultArray = []; //la liste qui va contenir tous les emails
  const logOut = async () => {
    try {
      await signOut(auth);
      navigate("/");
    } catch {
      alert("For some reasons we can't deconnect you from your account");
    }
  };

  const readResult = async () => {
    //console.log(currentUser.email);
    const q = query(
      users,
      where("mail", "==", currentUser.email) //on recupère tous les resultats dont name = à la valeur de l'input
    );
    const querySnapshot = await getDocs(q);
    querySnapshot.forEach((doc) => {
      resultArray.push(doc); //on ajoute tous les mails dans la liste
    });
    //console.log(resultArray[0].data());
    setAffichage(
      resultArray[0].data().name + " " + resultArray[0].data().surName
    );
  };
  readResult();
  return (
    <div className="container">
      <h1 className="text-light">
        Hi, <p></p>
      </h1>
      <button onClick={logOut}>deconnection</button>
    </div>
  );
}
