import { UserContext } from "../../../context/UserContext";
import { useContext, useState } from "react";
import { db } from "../../../firebase-config";
import { collection, query, where, getDocs } from "firebase/firestore"
import NavBar from "../../../components/NavBar";
import {useNavigate} from "react-router-dom";

export default function Research() {
    
  const recherche = useRef()

  const readResult = async () => {
    const q = query(
        users,
        where("classe", "==", recherche.current.value.toUpperCase()) //on recupère tous les resultats dont name = à la valeur de l'input
      );
      const querySnapshot = await getDocs(q);
      querySnapshot.forEach((doc) => {
        resultArray.push(doc.id); //on ajoute tous les mails dans la liste
      });
    
  };

  useEffect(() => {
    readResult();
  }, []);
  return (
    <>
        <input type="text" ref={recherche}></input>
        <button onClick={() => {console.log(recherche.current.value)}} >Rechercher</button>
    </>
  )
}
