import React from "react";

export default function Home() {
  let affichage = 0;

  const afficher1 = () => {
    affichage = 1;
    return <button onClick={afficher2}>Bouton2</button>;
  };

  const afficher2 = () => {
    affichage = 0;
    return <button onClick={afficher1}>Bouton1</button>;
  };
  if (affichage == 1) {
    afficher1();
  } else {
    afficher2();
  }
  return <div className="container-fluid  bg-dark"></div>;
}
